//JavaScript
var response=this.datasource;
var resultArray=response.results.Result;
var gwUpRfmObj={};
$(function () {
   function Paginator(items, page, per_page) {

                    var page = page || 1,
                            per_page = per_page || 10,
                            offset = (page - 1) * per_page,
                            paginatedItems = items.slice(offset).slice(0, per_page),
                            total_pages = Math.ceil(items.length / per_page);
                    return {
                        page: page,
                        per_page: per_page,
                        pre_page: page - 1 ? page - 1 : null,
                        next_page: (total_pages > page) ? page + 1 : null,
                        total: items.length,
                        total_pages: total_pages,
                        data: paginatedItems
                    };
                }


        //paginator div
		function displayPagination(objToPaginate)
		{
		var limitPerPage=objToPaginate.length/10;
		$("#paginationdiv .btn-group").html("");
		 $("#paginationdiv .btn-group").append('<button type="button" class="btn btn-outline-primary" id="previous" data-pnum="">Prev</button>');
                for (var i = 0; i <limitPerPage; i++)
                {
                    $("#paginationdiv .btn-group").append('<button type="button" class="btn btn-outline-primary">' + (i + 1) + '</button>');
                }
                $("#paginationdiv .btn-group").append('<button type="button" class="btn btn-outline-primary" id="next" data-nnum="">Next</button>');
		}

		//get Object for page	
            displayPagination(resultArray);   
				$(document).off("click","#paginationdiv .btn")
                $(document).on("click","#paginationdiv .btn",function (e) {
                    console.log(e);
                    var pageNumber;
                    $("#paginationdiv .btn").removeClass("active");
                    if (e.currentTarget.id === "previous")
                    {
                        pageNumber = parseInt(e.currentTarget.dataset.pnum);
                        $("#paginationdiv .btn:contains(" + e.currentTarget.dataset.pnum + ")").addClass("active");
                    } else if (e.currentTarget.id === "next")
                    {
                        pageNumber = parseInt(e.currentTarget.dataset.nnum);
                        $("#paginationdiv .btn:contains(" + e.currentTarget.dataset.nnum + ")").addClass("active");
                    } else {
                        $(e.target).addClass("active");
                        pageNumber = parseInt(e.currentTarget.innerText);
                    }
                    $("#previous").attr("data-pnum", pageNumber - 1);
                    $("#next").attr("data-nnum", pageNumber + 1);
                    if (pageNumber == 1)
                    {
                        $("#previous").attr('disabled', true);

                    } else {
                        $("#previous").attr('disabled', false);

                    }
					//console.log(parseInt(resultArray.length/10)+1);
                    if (pageNumber == (parseInt(resultArray.length/10)+1))
                    {
                        $("#next").attr('disabled', true);
                    } else {
                        $("#next").attr('disabled', false);
                    }

                    var objToShow = Paginator(resultArray, pageNumber, 10);
        //$("#paginationResultDiv").html(JSON.stringify(objToShow.data));
                    devicesDetails(objToShow.data);
                    function devicesDetails(listOfGateways) {
        //var listOfGateways=response.results.Result;
                        $("#accordion").html("");
                        for (i = 0; i < listOfGateways.length; i++) {

                            var gatewayCard = "<div class='card'>";
                            gatewayCard += "<div class='card-header bg-info'>";
                            gatewayCard += "<a class='collapsed card-link show' data-toggle='collapse' href='#sensorsOf_" + listOfGateways[i]["GatewayID"] + "'>";
                            gatewayCard += "Gateway - " + listOfGateways[i]["GatewayID"];
                            gatewayCard += "</a>"
                            gatewayCard += "</div>";
                            gatewayCard += "<div id='sensorsOf_" + listOfGateways[i]["GatewayID"] + "' class='collapse in' data-parent='#accordion'>";
                            gatewayCard += "<div class='card-body'>";
                            gatewayCard += "<table class='table-bordered'>";
                            gatewayCard += "<tr>";
                            gatewayCard += "<td><b>GatewayID: </b>" + listOfGateways[i].GatewayID + "</td>";
                            gatewayCard += "<td><b>GatewayName: </b>" + listOfGateways[i].GatewayName + "</td>";
                            gatewayCard += "<tr>";
                            gatewayCard += "<tr>";
                            gatewayCard += "<td><b>NetworkUUID: </b>" + listOfGateways[i].NetworkUUID + "</td>";
                            gatewayCard += "<td><b>LastCommunicationDate: </b>" + new Date(listOfGateways[i].LastCommunicationDate) + "</td>";
                            gatewayCard += "<tr>";
                            gatewayCard += "<tr>";
                            gatewayCard += "<td><b>HeartBeat: </b>" + listOfGateways[i].Heartbeat + "</td>";
                            gatewayCard += "<td><b>BatteryLevel: </b>" + listOfGateways[i].BatteryLevel + "</td>";
                            gatewayCard += "<tr>";
                            gatewayCard += "<tr>";
                            gatewayCard += "<td><b>SignalStrength: </b>" + listOfGateways[i].SignalStrength + "</td>";
                            gatewayCard += "<td><b>MAC Address: </b>" + listOfGateways[i].MAC_Address + "</td>";
                            gatewayCard += "<tr>";
                            gatewayCard += "</table>";
							gatewayCard +="<div class='mt-2 mb-2'>";
							gatewayCard +="<button class='btn btn-info gwFirmwareReform mr-2 float-right' data-gwid="+listOfGateways[i].GatewayID+" data-method='update'>Update Firmware</button>";
							gatewayCard +="<button class='btn btn-info gwFirmwareReform mr-2 float-right' data-gwid="+listOfGateways[i].GatewayID+" data-method='reform'>Reform</button>";
							gatewayCard +="</div>";
                            gatewayCard += "<h5>Sensors Information</h5><hr/>";
                            gatewayCard += "<table class='table-striped'>";
                            gatewayCard += "<thead>";
                            gatewayCard += "<tr>";
                            gatewayCard += "<th>SensorID</th>";
                            gatewayCard += "<th>SensorName</th>";
                            gatewayCard += "<th>SensorReading</th>";
                            gatewayCard += "<th>BatteryLevel</th>";
                            gatewayCard += "<th>SignalStrength</th>";
                            gatewayCard += "<th>Reading On</th>";
                            gatewayCard += "</tr>";
                            gatewayCard += " </thead>";
                            gatewayCard += " <tbody>";
                            for (j = 0; j < listOfGateways[i].Sensors.length; j++)
                            {
                                var sensorObj = listOfGateways[i].Sensors[j];
                                gatewayCard += "<tr>";
                                gatewayCard += "<td>" + sensorObj.SensorID + "</td>";
                                gatewayCard += "<td>" + sensorObj.SensorName + "</td>";
                                gatewayCard += "<td>" + sensorObj.CurrentReading + "</td>";

                                if (sensorObj.BatteryLevel >= 50 && sensorObj.BatteryLevel <= 100) {
                                    gatewayCard += "<td><i class='fa fa-battery-full' style='font-size:18px;color:green'></i><br/>" + sensorObj.BatteryLevel + "%</td>";
                                } else if (sensorObj.BatteryLevel >= 26 && sensorObj.BatteryLevel <= 49) {
                                    gatewayCard += "<td><i class='fa fa-battery-half' style='font-size:18px;color:amber'></i><br/>" + sensorObj.BatteryLevel + "%</td>";
                                } else if (sensorObj.BatteryLevel >= 1 && sensorObj.BatteryLevel <= 25) {
                                    gatewayCard += "<td><i class='fa fa-battery-quarter' style='font-size:18px;color:red'></i><br/>" + sensorObj.BatteryLevel + "%</td>";
                                } else {
                                    gatewayCard += "<td><i class='fa fa-battery-empty' style='font-size:18px;color:red'></i><br/>" + sensorObj.BatteryLevel + "%</td>";
                                }

                                if (sensorObj.SignalStrength >= 50 && sensorObj.SignalStrength <= 100) {
                                    gatewayCard += "<td><i class='fa fa-signal' style='font-size:18px;color:green'></i><br/>" + sensorObj.SignalStrength + "</td>";
                                } else if (sensorObj.SignalStrength >= 26 && sensorObj.SignalStrength <= 49) {
                                    gatewayCard += "<td><i class='fa fa-signal' style='font-size:18px;color:#FFC200'></i><br/>" + sensorObj.SignalStrength + "</td>";
                                } else if (sensorObj.SignalStrength >= 1 && sensorObj.SignalStrength <= 25) {
                                    gatewayCard += "<td><i class='fa fa-signal' style='font-size:18px;color:red'></i><br/>" + sensorObj.SignalStrength + "</td>";
                                } else {
                                    gatewayCard += "<td><i class='fa fa-signal' style='font-size:18px;color:black'></i><br/>" + sensorObj.SignalStrength + "</td>";
                                }


                                gatewayCard += "<td>" + (new Date(sensorObj.LastCommunicationDate)).toLocaleString()+ "</td>";
                                gatewayCard += "</tr>";
                            }
                            gatewayCard += " </tbody>";
                            gatewayCard += "</table>";
                            gatewayCard += "</div>";
                            gatewayCard += "</div>";
                            gatewayCard += "</div><br/>";
                            $("#accordion").append(gatewayCard);
                        }
